import React from "react";
import styled, { css } from "styled-components/macro";

const HeroSection = styled.section`
  height: 800px;
  max-height: 1100px;
  position: relative;
  overflow: hidden;
`;
const HeroWrapper = styled.section`
  width: 100%;
  height: 750px;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
  position: relative;
`;

const HeroSlide = styled.section``;
const HeroSlider = styled.section``;
const HeroImage = styled.section``;
const HeroContent = styled.section``;

const SlideShow = () => {
  return (
    <HeroSection>
      <HeroWrapper>
      {slides.map((slide,index)=>{
        return(
            <HeroSlide key={index}>
                <HeroSlider>
                    <HeroImage/>
                       <HeroContent>
                           <h1>
                               {slide.title}
                           </h1>
                       </HeroContent> 
                </HeroSlider>
            </HeroSlide>
        )
      })}
      </HeroWrapper>
    </HeroSection>
  );
};

export default SlideShow;
