import "./styles.css";

import React from "react";
import { Link } from "react-router-dom";

const CardItem = (props)=> {
  const { path, src, price, text, label } = props;
  return (
    <>
      <li className={"cardsItem"}>
        <Link className={"cards__item__link"} to={path}>
          <figure className={"cards__item__pic-wrap"} data-category={label}>
            <img src={src} alt={"Products "} className={"cards__item__img"} />
          </figure>
          <div className={"cards__item__info"}>
            <span className={"cards__item__text"}>{text}</span>
            <span className={"card__item__text"}>{price}</span>
          </div>
          <div className={"card__buy__btn__container"}>
            <button className={"add__to__bag__btn"}>Add to Bag</button>
          </div>
        </Link>
      </li>
    </>
  );
}

export default CardItem;
